package chapter05_스트래티지패턴.exercise.p06_1_code;

public class Item {
	private String name;
	private int price;
	
	
	
	public Item(String name, int price) {
		// TODO Auto-generated constructor stub
		this.name = name;
		this.price = price;
	}
	
	public String getName() {
		return name;
	}
	public int getPrice() {
		return price;
	}
	
	
}
